pragma solidity ^0.4.17;

/**
 * @title Ethereum-Lottery
 * @dev Simple lottery smart contract to run on the Ethereum
 * chain. Designed to (hopefully) work well with a web3 front-end.
 * Source of randomness comes from ethereum block hashes.
 *
 */

contract SOBP {

    struct oneBP {
        uint256 G;
        uint256 H;
        uint256 BP;
    }
    struct BPTask {
        address requester;
        uint256 G1;
        uint256 H1;
        uint256 BP1;
        uint256 G2;
        uint256 H2;
        uint256 BP2;
        uint256 G3;
        uint256 H3;
        uint256 BP3;
        bool state;
    }
    uint256 public random;
    uint64 public taskPrice = 5 finney;
    address public owner;

    oneBP [] public ranBP; 
    oneBP temper;
    BPTask [] public taskTable;
    BPTask temperTask;

     event _uploadTask(address indexed requester);
     event _updateRan(address indexed requester);
     event _executeTask(address indexed requester);
     event _getTask(address indexed requester);

    function SOBP() public{
      owner = msg.sender;
    }

    function() payable public {
      // for now, have ticket purchasing only through functions
      // for sanity purposes
      revert();
    }

    function updateRan(uint256 G, uint256 H, uint256 BP) public {
      require(msg.sender == owner);      
      temper.G = G;
      temper.H = H;
      temper.BP = BP;
      ranBP.push(temper);   
      _updateRan(msg.sender);  
    }

    function randomGen() internal returns (oneBP){
      require(ranBP.length > 0);
      random = uint64(sha256(block.timestamp, block.number, uint256(block.coinbase), block.gaslimit)) % ranBP.length;
      return ranBP[random];
    }

    function uploadTask(address requester, uint256 G1, uint256 H1, uint256 G2, uint256 H2) payable public returns (bool) {
      require(msg.value == taskPrice); 
      temperTask.requester = requester;
      temperTask.G1 = G1;
      temperTask.H1 = H1;
      temperTask.G2 = G2;
      temperTask.H2 = H2;
      temper = randomGen();
      temperTask.G3 = temper.G;
      temperTask.H3 = temper.H;
      temperTask.BP3 = temper.BP;
      temperTask.state = false;
      taskTable.push(temperTask);
      _uploadTask(msg.sender);
      return true;
    }

    function getTask() public view returns(address requester, uint256 G1, uint256 H1, uint256 G2, uint256 H2, uint256 G3, uint256 H3) {

      random = uint64(sha256(block.timestamp, block.number, uint256(block.coinbase), block.gaslimit)) % taskTable.length;
      while(taskTable[random].state)
      {
        random = uint64(sha256(block.timestamp, block.number, uint256(block.coinbase), block.gaslimit)) % taskTable.length;
      }
      requester = taskTable[random].requester;
      G1 = taskTable[random].G1;
      H1 = taskTable[random].H1;
      G2 = taskTable[random].G2;
      H2 = taskTable[random].H2;
      G3 = taskTable[random].G3;
      H3 = taskTable[random].H3;
      _getTask(msg.sender);

    }

    function executeTask (address requester, uint256 BP1, uint256 BP2, uint256 BP3) public returns (bool) {
         uint256 i;
         uint256 num;
         for(i = 0; i < taskTable.length; i++)
         {
            if(requester == taskTable[i].requester)
            {
              num = i;
              break;
            }
            else
            {
              return false;
            }
         }

         if(taskTable[num].BP3 == BP3)
         {
            taskTable[num].BP1 = BP1;
            taskTable[num].BP2 = BP2;
            taskTable[num].state = true;
            msg.sender.transfer(taskPrice);
         }
         else
         {
            taskTable[num].state = false;
            requester.transfer(taskPrice);
         }
	 _executeTask(msg.sender);
         return true;        

       }


    function getTaskLength() public view returns (uint256){
         return taskTable.length;
    }

    function getRanLength() public view returns (uint256){
         return ranBP.length;
    }

    function getRandom() public view returns (uint256){
         return random;
    }
}

